/**
 * @description
 * This element is used to specify an offset in pixels,
 * to be applied when drawing a tile from the related tileset. When not present, no offset is applied.
 */
export declare type TileOffset = {
    /**
     * Horizontal offset in pixels
     */
    x: number;
    /**
     * Vertical offset in pixels (positive is down)
     */
    y: number;
};
